import Head from "next/head";
import client from "../../client";

import Container from "react-bootstrap/Container";
import Image from "react-bootstrap/Image";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

const Tops = (props) => {
  const { products = [] } = props;
  return (
    <div>
      <Head>
        <title>OJOS | Tops</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>
      <h1 className="page-title">Tops</h1>
      <Container fluid="lg">
        <Row>
          {products.length ? (
            products.map((product) => (
              <Col xs={12} sm={6} md={6} lg={4}>
                <div className="product-section">
                  <a href={"/products/" + product.slug}>
                    <Image className="products" fluid src={product.imageUrl} />
                  </a>
                </div>
                <div className="product-description">
                  <a href="/products/:id" className="product-name">
                    {product.title}
                  </a>
                  <a href="/products/:id" className="product-price">
                    {product.price}
                  </a>
                </div>
              </Col>
            ))
          ) : (
            <p className="no-products-msg">
              No products right now, check back later!
            </p>
          )}
        </Row>
      </Container>
    </div>
  );
};

Tops.getInitialProps = async () => ({
  products: await client.fetch(`
    *[_type == "product" && categories[0]->title == "Tops"]{title, price, slug, "imageUrl": image.asset->url}
  `),
});

export default Tops;
