import Client from "shopify-buy";

const ShopContext = React.createContext();

const client = Client.buildClient({
  domain: "ojos-store.myshopify.com",
  storefrontAccessToken: "e52f8498c6bb5dea9c7c6670860b7224",
});

class ShopProvider extends React.Component {
  state = {
    products: [],
    product: {},
    checkout: {},
    checkoutTotal: 0,
  };

  componentDidMount() {
    this.createCheckout();
  }

  createCheckout = async () => {
    const checkout = await client.checkout.create();
    this.setState({ checkout: checkout });
  };

  addItemToCart = async (variantId, quantity) => {
    const lineItemsToAdd = [
      {
        variantId,
        quantity: parseInt(quantity, 10),
      },
    ];
    const checkout = await client.checkout.addLineItems(
      this.state.checkout.id,
      lineItemsToAdd
    );
    this.setState({ checkout: checkout });
  };

  fetchAllProducts = async () => {
    const products = await client.product.fetchAll();
    this.setState({ products: products });
  };

  fetchProductWithId = async (id) => {
    const product = await client.product.fetch(id);
    this.setState({ product: product });
  };

  incrementCheckoutTotal = async () => {
    this.setState({ checkoutTotal: checkoutTotal + 1 });
  };

  render() {
    return (
      <ShopContext.Provider value={{ ...this.state }}>
        {this.props.children}
      </ShopContext.Provider>
    );
  }
}

const ShopConsumer = ShopContext.Consumer;

export { ShopConsumer, ShopContext };

export default ShopProvider;
