import Head from "next/head";

import { ShopContext } from "../../context/shopContext";

//Sanity.io
import client from "../../client";
import urlFor from "../../imageUrlBuilder";

//Bootstrap
import Container from "react-bootstrap/Container";
import Image from "react-bootstrap/Image";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { useEffect } from "react";

const AllProducts = (props) => {
  // const { products = [] } = props;

  const { fetchAllProducts, products } = React.useContext(ShopContext);

  useEffect(() => {
    fetchAllProducts();
    return () => {};
  }, [fetchAllProducts]);

  if (!products) return <div>Loading...</div>;
  return (
    <div>
      <Head>
        <title>OJOS | All</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>
      <h1 className="page-title">All WE HAVE</h1>
      <Container fluid="lg">
        <Row>
          {products.length ? (
            products.map((product) => (
              <Col xs={12} sm={6} md={6} lg={4} key="">
                <div className="product-section">
                  <a href="/products/">
                    <Image className="products" fluid src="" />
                  </a>
                </div>
                <div className="product-description">
                  <a href="/products/:id" className="product-name">
                    {product.title}
                  </a>
                  <a href="/products/:id" className="product-price">
                    {product.price}
                  </a>
                </div>
              </Col>
            ))
          ) : (
            <div className="no-products-msg">
              <p>No products right now, check back later!</p>
            </div>
          )}
        </Row>
      </Container>
    </div>
  );
};

// AllProducts.getInitialProps = async () => ({
//   products: await client.fetch(`
//     *[_type == "product"]|order(_createdAt desc){title, price, slug, image}
//   `),
// });
export default AllProducts;
