import Head from "next/head";
import { ShopContext } from "../context/shopContext";

import Container from "react-bootstrap/Container";
import Image from "react-bootstrap/Image";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Spinner from "react-bootstrap/Spinner";

const Contact = () => {
  const { searchResults, fetchBySearch } = React.useContext(ShopContext);
  const [query, setQuery] = React.useState("");

  // React.useEffect(() => {
  //   return () => {};
  // }, [fetchBySearch]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const searchQuery = query;
    fetchBySearch(searchQuery);
  };

  const handleChange = (e) => {
    setQuery(e.target.value);
  };

  return (
    <div>
      <Head>
        <title>OJOS | Search</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>
      <form type="search" className="search-form" onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Search"
          className="search-form-input"
          value={query}
          onChange={handleChange}
        />
        <button type="submit" className="search-form-input-button">
          Go
        </button>
      </form>

      <Container fluid="lg">
        <Row>
          {collection.products.map((product) => (
            <Col xs={12} sm={6} md={6} lg={4} key={product.id}>
              <div className="product-section">
                <a href={`/products/${product.handle}`}>
                  <Image
                    className="products"
                    fluid
                    src={product.images[0].src}
                  />
                </a>
              </div>
              <div className="product-description">
                <a
                  href={`/products/${product.handle}`}
                  className="product-name"
                >
                  {product.title}
                </a>
                <a href="/products/:id" className="product-price">
                  {product.availableForSale
                    ? "$" + product.variants[0].price
                    : "Sold out"}
                </a>
              </div>
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
};

export default Contact;
