      <Container fluid>
        <Row>
          <Col xs={12} sm={12} md={6}>
            <div className="about-image-one">
              <Image
                fluid
                src="https://cdn.shopify.com/s/files/1/0021/9919/1618/files/IMG_6049_1200x.jpg?v=1596433120"
              />
            </div>
          </Col>
          <Col xs={12} sm={12} md={6}>
            <div className="about-paragraph">
